create table employees (date_of_birth date, version integer not null, created_date timestamp(6) with time zone not null, last_modified_date timestamp(6) with time zone not null, bank_account_bic varchar(11), phone varchar(15), address_number varchar(20), address_postal_code varchar(20), citizenship varchar(30), bank_account_iban varchar(34), address_city varchar(100), address_country varchar(100), address_state varchar(100), address_street varchar(100), bank_account_holder_name varchar(100), bank_account_institution_name varchar(100), health_insurance varchar(100), national_insurance_number varchar(100), place_of_birth varchar(100), tax_identification_number varchar(100), created_by_id varchar(255), created_by_username varchar(255) not null, email varchar(255), first_name varchar(255) not null, id varchar(255) not null, last_modified_by_id varchar(255), last_modified_by_username varchar(255) not null, last_name varchar(255) not null, marital_status varchar(255) check (marital_status in ('SINGLE','MARRIED','CIVIL','DIVORCED','WIDOWED')), user_id varchar(255) unique, primary key (id));
create table greetings (version integer not null, created_date timestamp(6) with time zone not null, last_modified_date timestamp(6) with time zone not null, created_by_id varchar(255), created_by_username varchar(255) not null, id varchar(255) not null, last_modified_by_id varchar(255), last_modified_by_username varchar(255) not null, message varchar(255) not null, primary key (id));
create table user_roles (role varchar(255) check (role in ('EXTERNAL','USER','MANAGER','ADMIN','SUPERUSER')), user_id varchar(255) not null);
create table users (is_enabled boolean not null, is_locked boolean not null, version integer not null, created_date timestamp(6) with time zone not null, last_modified_date timestamp(6) with time zone not null, username varchar(20) not null unique, created_by_id varchar(255), created_by_username varchar(255) not null, email varchar(255) not null, first_name varchar(255) not null, id varchar(255) not null, last_modified_by_id varchar(255), last_modified_by_username varchar(255) not null, last_name varchar(255) not null, password varchar(255) not null, primary key (id));
alter table if exists employees add constraint FK69x3vjuy1t5p18a5llb8h2fjx foreign key (user_id) references users;
alter table if exists user_roles add constraint FKhfh9dx7w3ubf1co1vdev94g3f foreign key (user_id) references users;
